import React, { useRef, useEffect, useState } from 'react';

const correctOTP = ["3", "1", "4", "1", "5", "9", "2", "6", "5", "3", "5", "8"];
var updatedOTP = correctOTP; 

function DigitInput({ numberOfDigits }) {

  // TODO:
  // - enforce only digits
  // - have a "correct" message (check mark next to the box)
  // - keep track of stream in a box

  const [otp, setOtp] = useState(new Array(numberOfDigits).fill(""));
  const [otpError, setOtpError] = useState(null);
  const [attempt, setAttempt] = useState(true);
  const otpBoxReference = useRef([]);

  const resetDigits = () => {
    let newArr = [...otp];
    for(let i = 0; i < numberOfDigits; i++){
      newArr[i] = "";
    }
    setOtp(newArr);
    otpBoxReference.current[0].focus();
  }

  function handleChange(value, index) {
    if(value === updatedOTP[index]){
      let newArr = [...otp];
      newArr[index] = value;
      setOtp(newArr);
      //setOtpError(null);
      setOtpError(updatedOTP[index]);

      if(value && index < numberOfDigits-1){
        otpBoxReference.current[index + 1].focus();
      }
      var element;
      if( (index + 1) % 6 === 0){
        // Update block to show current 6
        // Update updatedOTP to reflect next 6
        for(let i = 0; i < 6; i++){
          element = updatedOTP.shift();
          // construct "completed" string with element with loop
        }
        resetDigits();
        setAttempt(true);
      }
    }else{
      if(attempt){
        setOtpError("❌ Incorrect digit, one more try");
        setAttempt(false)
      }else{
        resetDigits();
        setOtpError(null);
        setAttempt(true);
        updatedOTP = correctOTP;
      }
    }
  }

  useEffect(() => {
    if(otp.join("") === ""){
      setOtpError(null);
    }
  }, [otp]);

  return (
    <article className="w-1/2">
      <p className="text-2xl font-medium text-white mt-12">pi memorizer</p>
      <p className="text-base text-white mt-6 mb-4">Enter digits of pi you know:</p>

     <div className='flex items-center gap-4'>
      {otp.map((digit, index)=>(
        <input key={index} value={digit} maxLength={1}  
        onChange={(e)=> handleChange(e.target.value, index)}
        ref={(reference) => (otpBoxReference.current[index] = reference)}
        className={`border w-20 h-auto text-white p-3 rounded-md block bg-black focus:border-2 focus:outline-none appearance-none`}
        />
      ))}

     </div>

      <p className={`text-lg text-white mt-4 ${otpError ? 'error-show' : ''}`}>{otpError}</p>
    </article>
  );
}

export default DigitInput;
