import React, { useRef, useEffect, useState } from 'react';

const correctOTP = ["3", "1", "4", "1", "5", "9", "2", "6", "5", "3", "5", "8"];

function DigitInput({ numberOfDigits }) {

  const [otp, setOtp] = useState(new Array(numberOfDigits).fill(""));
  const [prevOtp, setPrevOtp] = useState()
  const [otpError, setOtpError] = useState(null);
  const otpBoxReference = useRef([]);

  function handleChange(value, index) {
    let newArr = [...otp];
    newArr[index] = value;
    setOtp(newArr);

    if(value && index < numberOfDigits-1){
      otpBoxReference.current[index + 1].focus()
    }
  }

  // get arrow keys to work, what's with enter?
  // somehow you need to not move to the next box
  function handleBackspaceAndEnter(e, index) {
    if(e.key === "Backspace" && !e.target.value && index > 0){
        otpBoxReference.current[index - 1].focus()
    }
    if(e.key === "Enter" && e.target.value && index < numberOfDigits-1){
      otpBoxReference.current[index + 1].focus()
    }
  }

  // Define empty_index
  const isEmptyIndex = (element) => element === "";

  // TODO:
  // - enforce only digits
  // - have a "correct" message

  useEffect(() => {
    let empty_index;
    if(otp.join("") === ""){
      setOtpError(null);
    }else{
      empty_index = otp.findIndex(isEmptyIndex);
      if(empty_index === -1) empty_index = 6;
      if(otp.slice(0, empty_index).join("") !== correctOTP.slice(0, empty_index).join("")){
        // stop user from skipping a box
        setOtpError("❌ Wrong Digit Please Check Again");
      }else{
        setOtpError(null);
      }
    }
  }, [otp]);

  return (
    <article className="w-1/2">
      <p className="text-2xl font-medium text-white mt-12">pi memorizer</p>
      <p className="text-base text-white mt-6 mb-4">Enter digits of pi:</p>

     <div className='flex items-center gap-4'>
      {otp.map((digit, index)=>(
        <input key={index} value={digit} maxLength={1}  
        onChange={(e)=> handleChange(e.target.value, index)}
        onKeyUp={(e)=> handleBackspaceAndEnter(e, index)}
        ref={(reference) => (otpBoxReference.current[index] = reference)}
        className={`border w-20 h-auto text-white p-3 rounded-md block bg-black focus:border-2 focus:outline-none appearance-none`}
        />
      ))}

     </div>

      <p className={`text-lg text-white mt-4 ${otpError ? 'error-show' : ''}`}>{otpError}</p>
    </article>
  );
}

export default DigitInput;
